import { useContext, useState } from "react";
import { sendTweetService } from "../services";
import { AuthContext } from "../context/AuthContext";

export const NewTweet = (addTweet) => {
  const [error, setError] = useState("");
  const [sending, setSending] = useState(false);
  const [image, setImage] = useState();
  const { token } = useContext(AuthContext);

  const handleForm = async (e) => {
    e.preventDefault();

    try {
      setSending(true);

      const data = new FormData(e.target);
      const tweet = await sendTweetService({ data, token });
      addTweet(tweet);
      e.target.reset();
    } catch (error) {
      setError(error.message);
    } finally {
      setSending(false);
    }
  };

  return (
    <form onSubmit={handleForm}>
      <h1>Add New Tweet</h1>
      <fieldset>
        <label htmlFor="text"> Text</label>
        <input type="text" id="text" name="text" />
      </fieldset>
      <fieldset>
        <label htmlFor="image">image (optional)</label>
        <input
          type="file"
          id="image"
          name="image"
          acept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
        />
        {image ? (
          <figure>
            <img
              src={URL.ccreateObjectURL}
              alt="Previw"
              stlye={{ width: "100px" }}
            />
            Hay una imagen seleccionada
          </figure>
        ) : null}
      </fieldset>
      <button>Send Tweet</button>
      {sending ? <p>Sending tweet</p> : null}
      {error ? <p>{error} </p> : null}
    </form>
  );
};
