import { Link } from "react-router-dom";

export const Tweet = ({ tweet }) => {
  return (
    <article>
      <p>Tweet.text</p>

      {tweet.image ? (
        <img
          src={`${process.env.REACT_APP_BACKEND}/uploads/${tweet.image}`}
          alt={tweet.text}
        />
      ) : null}
      <p>
        By {tweet.email} on{" "}
        <Link to={`/tweet/${tweet.id}`}>
          {new Date(tweet.created_at).toLocaleString()}
        </Link>
      </p>
    </article>
  );
};

export default Tweet;
