import { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { deleteTweetService } from "../services";

export const Tweet = ({ tweet }) => {
  const { user, token } = useContext(AuthContext);
  const [error, setError] = useState();

  const deleteTweet = async (id) => {
    try {
      await deleteTweetService({ id, token });
    } catch (error) {
      setError(error.message);
    }
  };
  return (
    <article>
      <p>{Tweet.text}</p>

      {tweet.image ? (
        <img
          src={`${process.env.REACT_APP_BACKEND}/uploads/${tweet.image}`}
          alt={tweet.text}
        />
      ) : null}
      <p>
        By {tweet.email} on{" "}
        <Link to={`/tweet/${tweet.id}`}>
          {new Date(tweet.created_at).toLocaleString()}
        </Link>
      </p>
      {user && user.id === tweet.user_id ? (
        <section>
          <button onClick={() => deleteTweet(tweet.id)}>Deelte tweet</button>
          {error ? <p>{error}</p> : null}
        </section>
      ) : null}
    </article>
  );
};
